$(document).ready(function(){
    $('.thumbnail').hover(function () {
        $(this).find('.caption_1').hide();
        $(this).find('.caption_2').show();
    },function () {
        $(this).find('.caption_1').show();
        $(this).find('.caption_2').hide();
    });

    $('.icon-heart').hover(function () {
        $(this).find('.fa-stack-1x').addClass('fa-heart')
        $(this).find('.fa-stack-1x').removeClass('fa-heart-o')
    },function () {
        $(this).find('.fa-stack-1x').removeClass('fa-heart')
        $(this).find('.fa-stack-1x').addClass('fa-heart-o')
    });
    $('.icon-email').hover(function () {
        $(this).find('.fa-stack-1x').addClass('fa-envelope')
        $(this).find('.fa-stack-1x').removeClass('fa-envelope-o')
    },function () {
        $(this).find('.fa-stack-1x').removeClass('fa-envelope')
        $(this).find('.fa-stack-1x').addClass('fa-envelope-o')
    });


    $("#LAY_demo1").on("mouseover",'.thumbnail',function(){
        $(this).find('.caption_1').hide();
        $(this).find('.caption_2').show();
    });
    $("#LAY_demo1").on("mouseout",'.thumbnail',function(){
        $(this).find('.caption_1').show();
        $(this).find('.caption_2').hide();
    });
    $("#LAY_demo1").on("mouseover",'.icon-heart',function(){
        $(this).find('.fa-stack-1x').addClass('fa-heart')
        $(this).find('.fa-stack-1x').removeClass('fa-heart-o')
    });
    $("#LAY_demo1").on("mouseout",'.icon-heart',function(){
        $(this).find('.fa-stack-1x').removeClass('fa-heart')
        $(this).find('.fa-stack-1x').addClass('fa-heart-o')
    });
    $("#LAY_demo1").on("mouseover",'.icon-email',function(){
        $(this).find('.fa-stack-1x').addClass('fa-envelope')
        $(this).find('.fa-stack-1x').removeClass('fa-envelope-o')
    });
    $("#LAY_demo1").on("mouseout",'.icon-email',function(){
        $(this).find('.fa-stack-1x').removeClass('fa-envelope')
        $(this).find('.fa-stack-1x').addClass('fa-envelope-o')
    });


});

